import React, { useState, useEffect } from 'react';
import axios from './axios';

function Row({ title, fetchUrl }) {

    const [movies, setMovies] = useState([]);
    const base_url = "https://image.tmdb.org/t/p/original/";

    // A snippet of code which runs based on specific condition/variable
    useEffect(() => {
        // everytime row will load, this snippet of code will run
        async function fetchData() {
            const request = await axios.get(fetchUrl);
            console.log(request);
            setMovies(request.data.results);
            return request;
        }
        fetchData();
        // if [], run once when the row load (on page load), and don't run again
        // if you have a variable passed from outside the block, [fetchUrl] --> dependency
    }, [fetchUrl]);

    console.table(movies);

    return (
        <div className="row">
           <h2>{title}</h2>

           <div className="row__posters">
            {movies.map(movie => (
                <img src={`${base_url}${movie.poster_path}`} alt={movie.name}/>
            ))}
           </div>
            {/* container -> posters */}
        </div>
    )
}

export default Row;
